      }
    },
    // ç‚¹å‡»å¤šé€‰
    onChangeDay(val) {
      // åˆ¤æ–­å•é€‰ï¼Œå¤šé€‰ï¼Œè¿˜æ˜¯èŒƒå›´é€‰ï¼Œå¯¹åº”å€¼1.2.3.
      if (this.selectType === 1) {
        if (this.arr.length === 0) {
          val.checked = true;
          this.arr=[val]
        } else if (this.arr.length === 1) {
          if ((this.arr[0].year === val.year) && (this.arr[0].month === val.month) && (this.arr[0].day === val.day)) {
            this.arr = []
          } else {
            this.arr = []
            val.checked = true;
            this.arr.push(val);
          }
        } else {
          return
        }
      } else if (this.selectType === 2) {
        if (val.checked) {
          // å‰”é™¤
          val.checked = false;
          this.arr = this.arr.filter((ele) => {
            return !(
              ele.year === val.year &&
              ele.month === val.month &&
              ele.day === val.day
            );
          });
        } else {
          // æ·»åŠ 
          val.checked = true;
          this.arr.push(val);
        }
      } else if (this.selectType === 3) {
        // èŒƒå›´é€‰æ‹©ï¼Œ
        if (this.arr.length === 0) {
          val.checked = true;
          this.arr.push(val);
        } else if (this.arr.length === 1) {
          val.checked = true;
          this.arr.push(val);
          const arrS = getRangeDay(this.arr[0],this.arr[1])
          this.arr = []
          this.arr = arrS
        } else {
          this.arr = []
          val.checked = true;
          this.arr.push(val);
        }
      }
      this.$emit("selectedData", this.arr);
    },
    initParameter() {
      let currentYear = this.currentYear - 1;
      for (let p = 3; p >= 0; p--) {
        this.lastYear[p] = currentYear--;
      }
      currentYear = this.currentYear;
      for (let l = 0; l < 12; l++) {
        this.thisYear[l] = currentYear++;
      }
