  // å°†ç»™å®šçš„åˆ—è¡¨åˆ†ä¸ºä¸¤åŠ
  let num = Math.floor(len / 2);
  let left = MergeSort(array.slice(0, num));
  let right = MergeSort(array.slice(num, array.length));
  return merge(left, right);

  function merge(left, right) {
    let [l, r] = [0, 0];
    let result = [];
    // ä» left å’Œ right åŒºåŸŸä¸­å„ä¸ªå–å‡ºç¬¬ä¸€ä¸ªå…ƒç´ ï¼Œæ¯”è¾ƒå®ƒä»¬çš„å¤§å°
    while (l < left.length && r < right.length) {
      // å°†è¾ƒå°çš„å…ƒç´ æ·»åŠ åˆ°resultä¸­ï¼Œç„¶åä»è¾ƒå°å…ƒç´ æ‰€åœ¨çš„åŒºåŸŸå†…å–å‡ºä¸‹ä¸€ä¸ªå…ƒç´ ï¼Œç»§ç»­è¿›è¡Œæ¯”è¾ƒï¼›
      if (left[l] < right[r]) {
        result.push(left[l]);
        l++;
      } else {
        result.push(right[r]);
        r++;
      }
    }
    // å¦‚æœ left æˆ–è€… right æœ‰ä¸€æ–¹ä¸ºç©ºï¼Œåˆ™ç›´æ¥å°†å¦ä¸€æ–¹çš„æ‰€æœ‰å…ƒç´ ä¾æ¬¡æ·»åŠ åˆ°resultä¸­
    result = result.concat(left.slice(l, left.length));
    result = result.concat(right.slice(r, right.length));
    return result;
  }
}
