function preOrderOfBST(list) {
  if (list && list.length > 0) {
    // å‰åºéå†ï¼Œç¬¬ä¸€ä¸ªå€¼ä¸ºæ ¹èŠ‚ç‚¹
    var root = list[0];
    // æ‰¾åˆ°æ•°ç»„ä¸­ï¼Œç¬¬ä¸€ä¸ªæ¯”æ ¹èŠ‚ç‚¹å¤§çš„èŠ‚ç‚¹ï¼Œå³ä¸ºå³å­æ ‘çš„èŠ‚ç‚¹
    for (var i = 0; i < list.length; i++) {
